function char_unkn(char) {
  line = `"${char}" is not a valid character`
  return [
    line,
    line,
    line,
    line,
    line,
    line,
    line
  ];
}

function write(input) {
  var line_1 = ""
      line_2 = ""
      line_3 = ""
      line_4 = ""
      line_5 = ""
      line_6 = ""
      line_7 = "";
  var chars = input.split("")
  chars.forEach((item, i) => {
    switch (item) {
      case " ":
        chars[i] = char_spac;
        break;
      case "-":
        chars[i] = char_dash;
        break;
      case "/":
        chars[i] = char_rsls;
        break;
      case "?":
        chars[i] = char_ques;
        break;
      case ",":
        chars[i] = char_coma;
        break;
      case ".":
        chars[i] = char_perd;
        break;
      case "<":
        chars[i] = char_larw;
        break;
      case ">":
        chars[i] = char_rarw;
        break;
      case "`":
        chars[i] = char_btck;
        break;
      case "'":
        chars[i] = char_squt;
        break;
      case '"':
        chars[i] = char_quot;
        break;
      case ";":
        chars[i] = char_scln;
        break;
      case ":":
        chars[i] = char_coln;
        break;
      case "\\":
        chars[i] = char_lsls;
        break;
      case "|":
        chars[i] = char_lsls;
        break;
      case "[":
        chars[i] = char_lbrk;
        break;
      case "]":
        chars[i] = char_rbrk;
        break;
      case "{":
        chars[i] = char_lbrc;
        break;
      case "}":
        chars[i] = char_rbrc;
        break;
      case "=":
        chars[i] = char_equal;
        break;
      case "+":
        chars[i] = chars_plus;
        break;
      case "_":
        chars[i] = chars_undr;
        break;
      case "(":
        chars[i] = chars_lpar;
        break;
      case ")":
        chars[i] = char_rpar;
        break;
      case "*":
        chars[i] = char_star;
        break;
      case "&":
        chars[i] = char_ampr;
        break;
      case "^":
        chars[i] = char_crrt;
        break;
      case "%":
        chars[i] = char_prct;
        break;
      case "$":
        chars[i] = char_dolr;
        break;
      case "@":
        chars[i] = char_atsg;
        break;
      case "!":
        chars[i] = char_excl;
        break;
      case "~":
        chars[i] = char_tild;
        break;
      case "#":
        chars[i] = char_hash;
        break;
      default:
        try {
          chars[i] = eval(`char_${item.toLowerCase()}`)
        } catch {
          chars[i] = char_unkn(item.toLowerCase())
        }
        break;
    }
    line_1 += ` ${chars[i][0]}`
    line_2 += ` ${chars[i][1]}`
    line_3 += ` ${chars[i][2]}`
    line_4 += ` ${chars[i][3]}`
    line_5 += ` ${chars[i][4]}`
    line_6 += ` ${chars[i][5]}`
    line_7 += ` ${chars[i][6]}`
  });
  return `${line_1}\n${line_2}\n${line_3}\n${line_4}\n${line_5}\n${line_6}\n${line_7}`
}
module.exports = write
