
class FeatureToggles {
    static filterFeatures(modelFiles, features) {

        return modelFiles.filter(filename => {
            return !/^features\/.*\.(cds|csn)$/.test(filename) || features.find(feature => (new RegExp(`features/${feature}/.*.(cds|csn)`).test(filename)));
        });
    }
}

module.exports = FeatureToggles;