const MESSAGE = 'Linter error: ';

module.exports = class LinterError extends Error {
    constructor(warnings) {
        super(`${MESSAGE}\n${warnings.join('\n')}`);
        this.code = this.statusCode = 422;
        this.name = 'LinterError';
        this.warnings = warnings;
    }

    toJSON() {
        return { message: this.message, name: this.name, warnings: this.warnings };
    }

};
