class TenantMetadata {

    static init(initialList) {
        TenantMetadata.allTenants = initialList;
    }

    static get all() {
        return TenantMetadata.allTenants;
    }

    static update(existingTenants) {
        TenantMetadata.allTenants = TenantMetadata.allTenants.filter(tenant => existingTenants.includes(tenant.subscribedTenantId));
        return existingTenants.filter(id => !TenantMetadata.allTenants.find(tenant => tenant.subscribedTenantId === id));
    }

    static fill(missingMetadata) {
        TenantMetadata.allTenants = TenantMetadata.allTenants.concat(missingMetadata);
    }
}

module.exports = TenantMetadata;