'use strict';

var Manager = require('./lib/Manager');

exports.create = function (options, callback) {
  var manager = new Manager(options);
  manager.init(function (err) {
    if (err) {
      return callback(err);
    }

    manager.getAll(function (err) {
      if (err) {
        return callback(err);
      }

      callback(null, manager);
    });
  });
};
